import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function MetricsCollector() {
  const [metrics, setMetrics] = useState([]);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/metrics/collect');
        setMetrics(prev => {
          const updated = [...prev, ...res.data];
          return updated.slice(-10); // keep last 10 records
        });
      } catch (err) {
        console.error('Error fetching metrics:', err);
      }
    };

    fetchMetrics();
    const interval = setInterval(fetchMetrics, 5000);
    return () => clearInterval(interval);
  }, []);

  const labels = metrics.map((_, i) => `t${i + 1}`);

  const createChart = (label, data, color) => ({
    labels,
    datasets: [
      {
        label,
        data,
        borderColor: color,
        backgroundColor: `${color}33`,
        fill: true,
        tension: 0.3,
      },
    ],
  });

  return (
    <div className="container">
      <h1>ðŸ“Š Metrics Collector (Live)</h1>

      {metrics.length > 0 ? (
        <>
          {/* --- Row 1 --- */}
          <div className="chart-row">
            <div className="chart-card">
              <h3>Throughput (kbps)</h3>
              <Line data={createChart('Throughput (kbps)', metrics.map(m => m.throughput), '#28a745')} />
              <p className="chart-description">
                Shows how much data is transmitted successfully over the network in kilobits per second.
              </p>
            </div>

            <div className="chart-card">
              <h3>Delay (ms)</h3>
              <Line data={createChart('Delay (ms)', metrics.map(m => m.delay), '#007bff')} />
              <p className="chart-description">
                Measures the time taken for data packets to travel from source to destination in milliseconds.
              </p>
            </div>
          </div>

          {/* --- Row 2 --- */}
          <div className="chart-row">
            <div className="chart-card">
              <h3>Packet Loss (%)</h3>
              <Line data={createChart('Packet Loss (%)', metrics.map(m => m.packetLoss), '#dc3545')} />
              <p className="chart-description">
                Displays the percentage of packets lost during transmission, affecting network reliability.
              </p>
            </div>

            <div className="chart-card">
              <h3>Retransmissions</h3>
              <Line data={createChart('Retransmissions', metrics.map(m => m.retransmissions), '#fd7e14')} />
              <p className="chart-description">
                Indicates the number of packets resent due to errors or packet loss during data transfer.
              </p>
            </div>
          </div>

          {/* --- Row 3 (single graph) --- */}
          <div className="chart-row">
            <div className="chart-card">
              <h3>Congestion Level (%)</h3>
              <Line data={createChart('Congestion Level (%)', metrics.map(m => m.congestion), '#6f42c1')} />
              <p className="chart-description">
                Represents how busy or congested the network is, showing potential performance bottlenecks.
              </p>
            </div>
          </div>
        </>
      ) : (
        <p>Loading metrics...</p>
      )}
    </div>
  );
}

export default MetricsCollector;
