import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import SimulateForm from '../components/SimulateForm';

function PerformanceEngine() {
  
  const [metrics, setMetrics] = useState([]);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/metrics/collect');
        setMetrics(res.data);
      } catch (err) {
        console.error('Error fetching metrics:', err);
      }
    };

    fetchMetrics();
    const interval = setInterval(fetchMetrics, 5000); // Poll every 5s
    return () => clearInterval(interval);
  }, []);

  const chartData = {
    labels: metrics.map(m => new Date(m.timestamp).toLocaleTimeString()),
    datasets: [
      {
        label: 'Throughput (kbps)',
        data: metrics.map(m => m.throughput),
        borderColor: 'green',
        fill: false,
      },
      {
        label: 'Packet Loss (%)',
        data: metrics.map(m => m.packetLoss),
        borderColor: 'red',
        fill: false,
      },
    ],
  };

  return (
    <div className="container">
      <h1>ðŸ“Š Performance Engine</h1>

      {/* Simulate Form */}
      <SimulateForm onSimulate={fetchMetrics} />  {/* <- this renders the form */}

      {/* Chart */}
      {metrics.length > 0 && (
        <div className="chart">
          <Line data={chartData} />
        </div>
      )}
    </div>
  );
}

export default PerformanceEngine;
