 import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function PerformanceEngine() {
  const [metrics, setMetrics] = useState([]);
  const [protocol, setProtocol] = useState('TCP');
  const [latency, setLatency] = useState(50);
  const [bandwidth, setBandwidth] = useState(1000);
  const [errorRate, setErrorRate] = useState(1);
  const [showChart, setShowChart] = useState(false);
  const [showForm, setShowForm] = useState(true);

  const fetchMetrics = () => {
    axios.get('http://localhost:5000/api/metrics')
      .then(res => setMetrics(res.data));
  };

  const runSimulation = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/simulate/run', {
      protocol,
      latency: Number(latency),
      bandwidth: Number(bandwidth),
      errorRate: Number(errorRate),
    });
    fetchMetrics();
    setShowChart(true);
    setShowForm(false);
  };

  const chartData = {
    labels: metrics.map(m => new Date(m.timestamp).toLocaleTimeString()),
    datasets: [
      { label: 'Latency (ms)', data: metrics.map(m => m.latency), borderColor: 'blue', fill: false },
      { label: 'Throughput (kbps)', data: metrics.map(m => m.throughput), borderColor: 'green', fill: false },
      { label: 'Packet Loss (%)', data: metrics.map(m => m.packetLoss), borderColor: 'red', fill: false },
    ],
  };

  return (
    <div className="container">
      <h1>Protocol Performance Analyzer</h1>

      {showForm && (
        <form className="form-box" onSubmit={runSimulation}>
          <h3>ðŸ”§ Simulate Protocol</h3>

          <div className="form-group">
            <label>Protocol</label>
            <select value={protocol} onChange={e => setProtocol(e.target.value)}>
              <option value="TCP">TCP</option>
              <option value="UDP">UDP</option>
              <option value="ICMP">ICMP</option>
            </select>
          </div>

          <div className="form-group">
            <label>Latency (ms)</label>
            <input type="number" value={latency} onChange={e => setLatency(e.target.value)} />
          </div>

          <div className="form-group">
            <label>Bandwidth (kbps)</label>
            <input type="number" value={bandwidth} onChange={e => setBandwidth(e.target.value)} />
          </div>

          <div className="form-group">
            <label>Error Rate (%)</label>
            <input type="number" value={errorRate} onChange={e => setErrorRate(e.target.value)} />
          </div>

          <button type="submit">Run Simulation</button>
        </form>
      )}

      {showChart && metrics.length > 0 && (
        <div className="chart">
          <Line data={chartData} />
        </div>
      )}
    </div>
  );
}

export default PerformanceEngine;
