import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

function MetricsCollector() {
  const [metrics, setMetrics] = useState([]);
  const [status, setStatus] = useState('');
  const [stopped, setStopped] = useState(false);
  const chartRef = useRef(null);

  useEffect(() => {
    if (stopped) return; // stop if collection ended

    const fetchMetrics = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/metrics/collect');
        setMetrics(prev => {
          const updated = [...prev, ...res.data];
          if (updated.length >= 10) {
            analyzeProtocol(updated.slice(-10)); // analyze last 10
            setStopped(true); // stop collecting
          }
          return updated.slice(-10); // keep only last 10
        });
      } catch (err) {
        console.error('Error fetching metrics:', err);
      }
    };

    fetchMetrics();
    const interval = setInterval(fetchMetrics, 5000);
    return () => clearInterval(interval);
  }, [stopped]);

  // --- Analyze protocol performance ---
  const analyzeProtocol = (data) => {
    const avgDelay = data.reduce((a, b) => a + b.delay, 0) / data.length;
    const avgLoss = data.reduce((a, b) => a + b.packetLoss, 0) / data.length;
    const avgThroughput = data.reduce((a, b) => a + b.throughput, 0) / data.length;

    // Simple performance conditions
    if (avgDelay < 150 && avgLoss < 2 && avgThroughput > 800) {
      setStatus('✅ Protocol is working fine and stable.');
    } else {
      setStatus('⚠️ Protocol is unstable or facing issues.');
    }
  };

  const labels = metrics.map((_, i) => `t${i + 1}`);

  const createChart = (label, data, color) => ({
    labels,
    datasets: [
      {
        label,
        data,
        borderColor: color,
        backgroundColor: `${color}33`,
        fill: true,
        tension: 0.3,
      },
    ],
  });

  // --- Export metrics as CSV ---
  const exportCSV = () => {
    if (!metrics.length) return;
    const header = Object.keys(metrics[0]).join(',');
    const rows = metrics.map(m => Object.values(m).join(','));
    const csvContent = [header, ...rows].join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', `metrics_${Date.now()}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // --- Download PDF Report ---
  const downloadPDF = () => {
    const input = document.getElementById('metrics-report');
    html2canvas(input).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save(`metrics_report_${Date.now()}.pdf`);
    });
  };

  return (
    <div className="container">
      <h1>📊 Metrics Collector (Live)</h1>

      {metrics.length > 0 ? (
        <>
          <div id="metrics-report">
            <div className="charts-row">
              <div className="chart-card">
                <h3>Throughput (kbps)</h3>
                <Line data={createChart('Throughput (kbps)', metrics.map(m => m.throughput), '#28a745')} />
              </div>
              <div className="chart-card">
                <h3>Delay (ms)</h3>
                <Line data={createChart('Delay (ms)', metrics.map(m => m.delay), '#007bff')} />
              </div>
            </div>

            <div className="charts-row">
              <div className="chart-card">
                <h3>Packet Loss (%)</h3>
                <Line data={createChart('Packet Loss (%)', metrics.map(m => m.packetLoss), '#dc3545')} />
              </div>
              <div className="chart-card">
                <h3>Retransmissions</h3>
                <Line data={createChart('Retransmissions', metrics.map(m => m.retransmissions), '#fd7e14')} />
              </div>
            </div>

            <div className="charts-row">
              <div className="chart-card">
                <h3>Congestion Level (%)</h3>
                <Line data={createChart('Congestion Level (%)', metrics.map(m => m.congestion), '#6f42c1')} />
              </div>
            </div>
          </div>

          <div className="buttons-row">
            <button onClick={exportCSV} className="export-btn">📥 Export CSV</button>
            <button onClick={downloadPDF} className="export-btn">📝 Download PDF</button>
          </div>

          {status && (
            <div className="status-box">
              <h3>{status}</h3>
            </div>
          )}
        </>
      ) : (
        <p>Loading metrics...</p>
      )}
    </div>
  );
}

export default MetricsCollector;
