import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { io } from 'socket.io-client';
import 'chart.js/auto';

const socket = io('http://localhost:5000');

function VisualizationDashboard() {
  const [simulated, setSimulated] = useState([]);
  const [realtime, setRealtime] = useState([]);

  // ---- Simulated Random Data ----
  useEffect(() => {
    const generateSimulated = () => {
      setSimulated((prev) => {
        const newData = {
          time: `T${prev.length + 1}`,
          throughput: Math.floor(Math.random() * 1000) + 500, // 500‚Äì1500 kbps
          delay: Math.floor(Math.random() * 100) + 20,        // 20‚Äì120 ms
          packetLoss: Math.random() * 5,                      // 0‚Äì5%
        };
        const updated = [...prev, newData];
        return updated.slice(-10); // keep only last 10 points
      });
    };

    // Generate every 3 seconds
    const interval = setInterval(generateSimulated, 3000);
    return () => clearInterval(interval);
  }, []);

  // ---- Real-time Data from Socket.IO ----
  useEffect(() => {
    socket.on('protocolMetrics', (newMetrics) => {
      setRealtime((prev) => {
        const mapped = newMetrics.map((m, i) => ({
          time: `Live${prev.length + i + 1}`,
          throughput: m.throughput || 0,
          delay: m.delay || 0,
          packetLoss: m.packetLoss || 0,
        }));
        const updated = [...prev, ...mapped];
        return updated.slice(-10); // keep last 10 points
      });
    });

    return () => socket.disconnect();
  }, []);

  // ---- Helper Function ----
  const extract = (arr, key) => arr.map((m) => m[key]);

  // ---- Simulated Chart ----
  const simulatedData = {
    labels: simulated.map((s) => s.time),
    datasets: [
      {
        label: 'Simulated Throughput (kbps)',
        data: extract(simulated, 'throughput'),
        borderColor: '#28a745',
        fill: false,
        tension: 0.3,
      },
      {
        label: 'Simulated Delay (ms)',
        data: extract(simulated, 'delay'),
        borderColor: '#ff9900',
        fill: false,
        tension: 0.3,
      },
      {
        label: 'Simulated Packet Loss (%)',
        data: extract(simulated, 'packetLoss'),
        borderColor: '#6f42c1',
        fill: false,
        tension: 0.3,
      },
    ],
  };

  // ---- Real-time Chart ----
  const realtimeData = {
    labels: realtime.map((r) => r.time),
    datasets: [
      {
        label: 'Real-time Throughput (kbps)',
        data: extract(realtime, 'throughput'),
        borderColor: '#007bff',
        fill: false,
        tension: 0.3,
      },
      {
        label: 'Real-time Delay (ms)',
        data: extract(realtime, 'delay'),
        borderColor: '#dc3545',
        fill: false,
        tension: 0.3,
      },
      {
        label: 'Real-time Packet Loss (%)',
        data: extract(realtime, 'packetLoss'),
        borderColor: '#17a2b8',
        fill: false,
        tension: 0.3,
      },
    ],
  };

  return (
    <div className="container">
      <h1>üìä Visualization Dashboard</h1>
      <p>Simulated vs Real-Time Network Performance</p>

      <div className="chart">
        <h3>üß™ Simulated Data </h3>
        <Line data={simulatedData} />
      </div>

      <div className="chart" style={{ marginTop: '40px' }}>
        <h3>‚ö° Real-Time Data </h3>
        <Line data={realtimeData} />
      </div>

      {realtime.length === 0 && (
        <p>‚è≥ Waiting for live data from backend...</p>
      )}
    </div>
  );
}

export default VisualizationDashboard;
