import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import jsPDF from "jspdf";

const MetricsCollector = ({ protocol }) => {
  const [metrics, setMetrics] = useState([]);
  const [socketInstance, setSocketInstance] = useState(null);

  // Function to generate PDF
  const generatePDFReport = (data) => {
    const doc = new jsPDF();
    doc.setFont("helvetica", "bold");
    doc.setFontSize(18);
    doc.setTextColor(0, 51, 102);
    doc.text("ðŸ“˜ Summary Report", 20, 20);

    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(0, 0, 0); // Dark text

    doc.text(`Protocol: ${data.protocol}`, 20, 35);

    doc.setFont("helvetica", "bold");
    doc.text("Average Delay:", 20, 45);
    doc.setFont("helvetica", "normal");
    doc.text(`${data.avgDelay.toFixed(2)} ms`, 70, 45);

    doc.setFont("helvetica", "bold");
    doc.text("Average Packet Loss:", 20, 55);
    doc.setFont("helvetica", "normal");
    doc.text(`${data.avgPacketLoss.toFixed(2)} %`, 70, 55);

    doc.setFont("helvetica", "bold");
    doc.text("Average Throughput:", 20, 65);
    doc.setFont("helvetica", "normal");
    doc.text(`${data.avgThroughput.toFixed(2)} kbps`, 70, 65);

    const isWorking =
      data.avgDelay < 100 && data.avgPacketLoss < 2 && data.avgThroughput > 0.1;

    doc.setFont("helvetica", "bold");
    doc.text("Conclusion:", 20, 80);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(isWorking ? 0 : 200, isWorking ? 128 : 0, 0); // Green/Red
    doc.text(
      isWorking
        ? "âœ… Protocol is working fine and stable."
        : "âš ï¸ Protocol is not working properly or unstable.",
      50,
      80
    );

    doc.save("Network_Report.pdf");
  };

  useEffect(() => {
    const socket = io("http://localhost:5000");
    setSocketInstance(socket);

    socket.on("metricsData", (data) => {
      setMetrics((prev) => {
        const updated = [...prev, data];
        if (updated.length >= 10) {
          socket.disconnect();

          // Calculate averages
          const avgDelay = updated.reduce((a, b) => a + b.delay, 0) / updated.length;
          const avgPacketLoss =
            updated.reduce((a, b) => a + b.packetLoss, 0) / updated.length;
          const avgThroughput =
            updated.reduce((a, b) => a + b.throughput, 0) / updated.length;

          // Auto-generate PDF
          generatePDFReport({
            protocol,
            avgDelay,
            avgPacketLoss,
            avgThroughput,
          });
        }
        return updated.slice(-10); // Keep only 10 latest values
      });
    });

    return () => socket.disconnect();
  }, [protocol]);

  return (
    <div className="metrics-container">
      <h2>Real-Time Metrics for {protocol}</h2>
      <ul>
        {metrics.map((m, idx) => (
          <li key={idx}>
            Delay: {m.delay} ms | Packet Loss: {m.packetLoss}% | Throughput:{" "}
            {m.throughput} kbps
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MetricsCollector;
