import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

function VisualizationDashboard() {
  const [scenarios, setScenarios] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/dashboard/scenarios')
      .then(res => setScenarios(res.data))
      .catch(err => console.error('Error loading dashboard data:', err));
  }, []);

  const labels = scenarios.map(s => s.name);

  const chartData = (metric, color) => ({
    labels,
    datasets: [
      {
        label: metric,
        data: scenarios.map(s => s[metric]),
        backgroundColor: color,
      },
    ],
  });

  return (
    <div className="container">
      <h1>ðŸ“ˆ Visualization Dashboard</h1>

      {scenarios.length > 0 ? (
        <>
          <div className="chart">
            <Bar data={chartData('throughput', '#28a745')} />
          </div>
          <div className="chart">
            <Bar data={chartData('delay', '#007bff')} />
          </div>
          <div className="chart">
            <Bar data={chartData('packetLoss', '#dc3545')} />
          </div>
          <div className="chart">
            <Bar data={chartData('retransmissions', '#fd7e14')} />
          </div>
          <div className="chart">
            <Bar data={chartData('congestion', '#6f42c1')} />
          </div>
        </>
      ) : (
        <p>Loading scenario data...</p>
      )}
    </div>
  );
}

export default VisualizationDashboard;
