import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import SimulateForm from '../components/SimulateForm';
import 'chart.js/auto';

function PerformanceEngine() {
  const [metrics, setMetrics] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchMetrics = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:5000/api/metrics/collect');
      setMetrics(res.data || []);
    } catch (err) {
      console.error('Error fetching metrics:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMetrics();
  }, []);

  const chartData = {
    labels: metrics.map((m, i) => `t${i + 1}`),
    datasets: [
      {
        label: 'Latency (ms)',
        data: metrics.map(m => m.latency),
        borderColor: '#0078d4',
        fill: false,
        tension: 0.3,
      },
      {
        label: 'Throughput (kbps)',
        data: metrics.map(m => m.throughput),
        borderColor: '#28a745',
        fill: false,
        tension: 0.3,
      },
      {
        label: 'Packet Loss (%)',
        data: metrics.map(m => m.packetLoss),
        borderColor: '#dc3545',
        fill: false,
        tension: 0.3,
      },
    ],
  };

  return (
    <div className="container">
      <h1>ðŸ“Š Performance Engine</h1>

      <SimulateForm onSimulate={fetchMetrics} />

      {loading && <p>Loading chart...</p>}

      {metrics.length > 0 && (
        <div className="chart">
          <Line data={chartData} />
        </div>
      )}
    </div>
  );
}

export default PerformanceEngine;
